import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import { List, Header } from "semantic-ui-react";
import { useTable } from 'react-table';

class CreateData extends React.Component {
  constructor(props) {
    super(props);
    this.state = {name: '', id: '', points: ''};
    // This binding is necessary to make `this` work in the callback
    this.handleName = this.handleName.bind(this);
    this.handleID = this.handleID.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handlePoints = this.handlePoints.bind(this);

  }

  handleName(event) {
    this.setState({name: event.target.value});
  }

  handleID(event) {
    this.setState({id: event.target.value});
  }

  handlePoints(event) {
    this.setState({points: event.target.value});
  }


  handleSubmit(event) {
    if (this.state.name == '' || this.state.id == '' || this.state.points == ''){
      alert('The query cannot be empty')
    } else if (isNaN(this.state.id) || isNaN(this.state.points)) {
      alert('ID and Points must be integers')
    } else {
      var newStudent = {
        name: this.state.name,
        id: Number(this.state.id),
        points: Number(this.state.points)
      }
      const response = fetch('/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newStudent)
      });

      this.props.onNewStudent(newStudent)

      this.setState({name: ''});
      this.setState({id: ''});
      this.setState({points: ''});
    }
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        Create:
        <label>
          Name:
          <input type="text" value={this.state.name} onChange={this.handleName}/>
        </label>
        <label>
          ID:
          <input type="text" value={this.state.id} onChange={this.handleID}/>
        </label>
        <label>
          Points:
          <input type="text" value={this.state.points} onChange={this.handlePoints}/>
        </label>
        <input type="submit" value="Add" />
      </form>
    );
  }
}

export const Students = ({ students }) => {
  const columnts = React.useMemo(
    () => [{
      columns: [{
        Header: 'Name',
        accessor: 'name',
      }, {
        Header: 'ID',
        accessor: 'id',
      }, {
        Header: 'Points',
        accessor: 'points',
      }, ],
    }, []]
  )
  return (
    <List>
      Name:
      {students.map(student => {
        return (
          <List.Item key = {student.name}>
            <Header>
              {student.name}
              {student.id}
              {student.points}
            </Header>
          </List.Item>

        )
      })}
    </List>
  );
}

function GetLines(props) {
  return (
    <div className="GetLines">  
      <Students students={props.students} />
    </div>
  );
}

function UpdateStudent(props) {
  return (
    <div>

    </div>
  );
}

function DeleteStudent(props) {
  return (
    <div>

    </div>
  );
}

function App() {
  // used to have and array of students
  const [students, setStudents] = useState([]);
  useEffect(() => {
    fetch("/data").then(response =>
      response.json().then(data => {
        setStudents(data.rows);
      })
    );
  }, []); 

  // console.log(students);

  return (
    <div className='table'>
      <CreateData onNewStudent={student => setStudents(currentStudents => [...currentStudents, student])}/>
      <GetLines students={students}/>
      <UpdateStudent students={students}/>
      <DeleteStudent students={students}/>
    </div>
  );
}

export default App;
