import React from 'react';
import { useTable, useRowSelect } from 'react-table';
import { FormButton } from 'semantic-ui-react';


class DeleteStudent extends React.Component {
  constructor(props){
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    if(this.props.selected.length === 0) {
        alert('Select rows to be deleted');
    } else {
        this.props.selected.forEach(row => {
            var removeStudent = {
                id: Number(row.id)
            }
            const response = fetch('/remove', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json'
                },
                body: JSON.stringify(removeStudent)
            });
            this.props.onRemoveStudent(row);
        });
    }
    event.preventDefault();
  }

  render(){
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input type="submit" value="Remove Selected" />
        </form>
      </div>
    );
  }
}

class UpdateStudent extends React.Component {
  constructor(props){
    super(props);
    this.state = {showChange: false}
    this.handleUpdate = this.handleUpdate.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    
  }

  showConfirm(){
      if(this.state.showChange){
        return (
        <form onSubmit={this.handleSubmit}>
          Create:
          <label>
            Name:
            <input type="text" value={this.state.name} onChange={this.handleName}/>
          </label>
          <label>
            ID:
            <input type="text" value={this.state.id} onChange={this.handleID}/>
          </label>
          <label>
            Points:
            <input type="text" value={this.state.points} onChange={this.handlePoints}/>
          </label>
          <input type="submit" value="Add" />
        </form>
        ); 
      } else {
        return null;
      }
  }

  handleUpdate(event) {
    if(this.props.selected.length !== 1){
        alert('One row must be selected')
    }  else {
        this.setState()
    }
    event.preventDefault();
  }

  handleSubmit(event) {
    if(this.props.selected.length === 0) {
      alert('Select rows to be deleted');
    } else {
      this.props.selected.forEach(row => {
        var removeStudent = {
          id: Number(row.id)
        }
        const response = fetch('/update', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(removeStudent)
        });
        this.props.onRemoveStudent(row);
      });
    }
    event.preventDefault();
  }

  render(){
      return (
      <div>
        <form onSubmit={this.handleUpdate}>
          <input type="submit" value="Update Selected" />
        </form>
        
      </div>
    );
  }
}

const IndeterminateCheckbox = React.forwardRef(
    ({ indeterminate, ...rest }, ref) => {
        const defaultRef = React.useRef()
        const resolvedRef = ref || defaultRef
    
        React.useEffect(() => {
          resolvedRef.current.indeterminate = indeterminate
        }, [resolvedRef, indeterminate])
    
        return (
          <>
            <input type="checkbox" ref={resolvedRef} {...rest} />
          </>
        )
      }
)

function Students(props){
  const data = React.useMemo(() => props.students);
  const columns = React.useMemo(
    () => [
      {
        Header: 'Name',
        accessor: 'name',
      }, {
        Header: 'ID',
        accessor: 'id',
      }, {
        Header: 'Points',
        accessor: 'points',
      },
    ], []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    selectedFlatRows,
    state: { selectedRowIds },
  } = useTable({columns, data},
    useRowSelect,
    hooks => {
        hooks.visibleColumns.push(columns => [{
            id: 'selection',
            Header: ({ getToggleAllRowsSelectedProps }) => (
                <div>
                    <IndeterminateCheckbox {...getToggleAllRowsSelectedProps()} />
                </div>
            ),
            Cell: ({ row }) => (
                <div>
                    <IndeterminateCheckbox {...row.getToggleRowSelectedProps()} />
                </div>
            ),
        },...columns,])
    });

  // renders ui for table
  return (
    <>
      <table {...getTableProps()}>
       <thead> 
         {headerGroups.map(headerGroup => (
           <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map(column => (
              <th {...column.getHeaderProps()}>
                {column.render('Header')}
              </th>
            ))}
           </tr>
         ))}
       </thead>
       <tbody {...getTableBodyProps()}>
         {rows.map(row => {
           prepareRow(row)
           return (
            <tr {...row.getRowProps()}>
              {row.cells.map(cell => {
                return (
                  <td {...cell.getCellProps()}>
                    {cell.render('Cell')}
                  </td>
                )
              })}
            </tr>
           )
         })}
       </tbody>
      </table>
      <DeleteStudent 
        selected = {selectedFlatRows.map(d => d.original)} 
        onRemoveStudent= {props.onRemoveStudent}
      />
      <UpdateStudent
        selected = {selectedFlatRows.map(d => d.original)}
        onRemoveStudent= {props.onRemoveStudent}
        onNewStudent= {props.onNewStudent}
      />
    </>
  );
}

export default Students;
