import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
/*
ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
*/

class CreateData extends React.Component {
  constructor(props) {
    super(props);
    this.state = {name: '', id: '', points: ''};
    // This binding is necessary to make `this` work in the callback
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    alert('Added Name:' + this.state.name + ' ID: ' + this.state.id + ' Points: ' + this.state.points);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.name}/>
        </label>
        <label>
          ID:
          <input type="text" value={this.state.id}/>
        </label>
        <label>
          Points:
          <input type="text" value={this.state.points}/>
        </label>
        <input type="submit" value="Add" />
      </form>
    );
  }
}

ReactDOM.render(
  <CreateData />,
  document.getElementById('root')
);
